competences.map(item => {
    competencesSection.innerHTML +=
        `<div class="comp">
            ${showIcon('skills', item.icon)}
            <p>${item.title}</p>
            <div class='progress'>
                <div class='progress-value' style="width:${0}%;height:100%"></div>
            </div>
        </div>`
})

const progressValues = document.querySelectorAll('.progress-value');
window.addEventListener('load', ()=>{
    loader.remove();
    setTimeout(()=>{
        for(let i = 0; i < competences.length; i++){
            progressValues[i].style=`width:${competences[i].level}%;height:100%`;
        }
    }, 500);
})

projectsData.map(item => {
    if (!item.dev) {
        projectSection.innerHTML += 
    `<div class="project">
            <div class="item-img-container">
                <img class="item-img" src="${item.cover}" alt="${item.title}">
            </div>
            <div class='project-data'>
                <h3 class='project-name'>${item.title}</h3>
                <div class='project-skills-icons'>${item.skills.map(i=>showIcon('skills', i)).join('')}</div>
                <p class='project-description'>${item.description}</p>
            </div>
            <a class='integral-link' href=${item.link}>
        </div>`
    }
});

technos.map(item => {
    technosSection.innerHTML += `<div class="tool ${item.class}">
            ${item.svg}
                <p>${item.title}</p>
        </div>`
});

technosElements = document.querySelectorAll('.tool');
technosElements.forEach(item=>{
    console.log(item);
    item.firstElementChild.addEventListener('mouseover', handleMouseOver);
    item.firstElementChild.addEventListener('mouseout', handleMouseOut)
})

function handleMouseOver(e){
    e.stopPropagation();
    e.target.classList.add('active')
}
function handleMouseOut(e){
    e.stopPropagation();
    e.target.classList.remove('active');
}

function showIcon(type, name) {
    if (type === 'skills') {
        return icons.skills[name];
    } else if (type === 'technos') {
        return icons.technos[name];
    }
}
